<?php require 'db.php'; if ($_SERVER["REQUEST_METHOD"] == "POST") { $email = $conn->real_escape_string($_POST['email']); $code = $conn->real_escape_string($_POST['code']); $check = $conn->query("SELECT * FROM users WHERE email='$email' AND verification_code='$code'"); if ($check->num_rows === 1) { $conn->query("UPDATE users SET is_verified=1, verification_code=NULL WHERE email='$email'"); echo "✅ සත්‍යාපනය සාර්ථකයි! දැන් ඔබට පිවිසිය හැක."; echo "<br><a href='index.php'>Login Page</a>"; } else { echo "❌ සත්‍යාපන කේතය හෝ email එක වැරදියි."; } } ?> <!-- HTML Form --> <!DOCTYPE html> <html lang="si"> <head> <meta charset="UTF-8"> <title>සත්‍යාපනය</title> <link rel="stylesheet" href="assets/style.css"> </head> <body> <h2>සත්‍යාපන කේතය ඇතුළත් කරන්න</h2> <form method="POST"> <input type="email" name="email" placeholder="ඔබේ Email" required><br> <input type="text" name="code" placeholder="සත්‍යාපන කේතය" required><br> <button type="submit">Verify</button> </form> </body> </html>
